package RAMDA_OperatorMinbymaxbyExample;

public class Fruit {
		String name;
		int price;
		
		public Fruit(String name, int price) {
			// TODO Auto-generated constructor stub
			this.name = name;
			this.price = price;
		}

		public String getName() {
			return name;
		}

		public int getPrice() {
			return price;
		}
		
		
}
